mapper.select:
select id, name, school_id as schoolId
from board
where school_id = #{schoolId}

select id, name, school_id as schoolId
from board
where board_id = #{boardId}

select id, user_id as userId, board_id as boardId
from boardfav
where user_id = #{userId}
and board_id = #{boardId}

mapper.delete:
delete from boardfav
where user_id = #{userId}
and board_id = #{boardId}
